import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  public rname:string="";
  public showList:any=[];
  public recipeinfo : any=[{
    type:'breakfast',
    name:"tomato and eggs",
    materials:['egg','tomato'],
    intro:"cook egg and tomato ",
    image : ""
  },
  {
    type:'lunch',
    name:"steak and onion",
    materials:['steak','onion'],
    intro:"cook steak and onion ",
    image:""
  },
  {
    type:'dessert',
    name:"cake",
    materials:['stawberry','flour','egg'],
    intro:"cook strawberry and flour ",
    image:""
  }
]

  constructor() { }

  ngOnInit(): void {
  }

  SearchByName(){
    for( let item of this.recipeinfo)
    {
      if(item.name == this.rname){
        this.showList.push(item);
        
      }
    }
    
     return this.showList;
  }
}
